package  com.algos.li03_slidingwindow;

import java.util.HashMap;

public class L567_PermutationInString {

	public static void main(String[] args) {
		L567_PermutationInString obj = new L567_PermutationInString();

		String s1 = "";
		String s2 = "";
		//String s2 = "";
		
		System.out.println(obj.checkInclusion(s1, s2));
	}

	// Problem: https://leetcode.com/problems/permutation-in-string/
	// Idea: Compare the freq. Load first m chars to resp maps and compare. 
	// Then subsequently remove last char from s2 and load new char and compare everytime.
	public boolean checkInclusion(String s1, String s2) {
		int m = s1.length(), n = s2.length(); 
		if(m > n) 
			return false;
		
		HashMap<Character, Integer> s1map = new HashMap<>();
		HashMap<Character, Integer> s2map = new HashMap<>();
		for(int i=0; i<m; i++) {
			s1map.put(s1.charAt(i), s1map.getOrDefault(s1.charAt(i), 0)+1);
            s2map.put(s2.charAt(i), s2map.getOrDefault(s2.charAt(i), 0)+1);
		}
		if(matching(s1map, s2map)) {
			return true;
		}
		
		for(int r=m; r<n; r++) {
			int l = r-m;
            s2map.put(s2.charAt(r), s2map.getOrDefault(s2.charAt(r), 0)+1);    
            s2map.put(s2.charAt(l), s2map.get(s2.charAt(l))-1);      
			
			if(matching(s1map, s2map)) {
				return true;
			}
		}
		
		return false;
	}
	
	private boolean matching(HashMap<Character, Integer> s1map, HashMap<Character, Integer> s2map) {
		for(char key: s1map.keySet()) {
			if(s1map.get(key) - s2map.getOrDefault(key, 0) != 0) {
				return false;
			}
		}
		
		return true;
	}

}
